<!DOCTYPE html><html>    <head>        <meta charset="utf-8" />        <link rel="stylesheet" href="style.css" />        <title>Test</title>		<script src="jquery.js"></script>		<script src='jquery.timers.js'></script>		<script src="snake.js"></script>		<script>			$( function()			{				// Global Variables Definition				play = 1;					// Bool Play/Stop				menu = 0;				food = 0;					// Bool Food Poping				size = 0; 					// Size of Snake				dirSnake = {top: '-=20'};	// First direction of the Snake				dirSnakeCell = {top: '-=20'}; // Cell direction of the Snake								haut = 1;				bas = 0;				gauche = 0;				droite = 0;								// Initialisation of the game - Menu				setInterval( function() { showMenu(menu) },100);								// Snake's Generation				$('#jeu').append('<div id="carre"></div>');												// Keyboard Move				$(window).keydown( function(event)				{					deplacementJoueur(event.which);				})				// Mouse Move				$('div[id^="pad_"]').click( function()				{					var pad = parseInt($(this).attr('class'));						deplacementJoueurMouse(pad);				})				// Snake move				setInterval( function() { deplacementSnake(dirSnake,size) },100);				//setInterval( function() { deplacementSnakeCell(size) },100);																											// Food Popping				setInterval(popFood,1000);				setInterval( function() { eatFood(size) },10);												// Collision Wall & Cell				setInterval(collisionMur,10);				setInterval( function() { collisionCell(size) },10);				setInterval(gameOver, 200);				// Debug				setInterval(positionJoueur,1);				setInterval( function() { getFood(food,size) },10);			})		</script>    </head>	    <body>		<div id="background">		<div id="background_top">			<img class="gameover" style="position:absolute;z-index:5;display:none;" src="img/gameover.png"/>			<img src="img/background-top.png"/>						<div id="pad">				<div class="38" id="pad_top"></div>				<div class="40" id="pad_bot"></div>				<div class="37" id="pad_left"></div>				<div class="39" id="pad_right"></div>			</div>		</div>			<div id="zone_jeu">			<div id="section">				<div id="menu" style="display: block;"></div>			<table id="taille" style="display:block;">				<tr>					<td>Size:</td>					<td><div id="size"></div></td>				</tr>			</table>		</div>				<div id="jeu"></div>			</div>			<div style="text-align:center;" id="zone_info"></div>		</div>    </body></html>
